import{_ as t,c as e,o as l,ak as r}from"./chunks/framework.DgtJxay-.js";const b=JSON.parse('{"title":"📢 服务器公告：车站建设推荐性标准","description":"","frontmatter":{},"headers":[],"relativePath":"nav/index.md","filePath":"nav/index.md","lastUpdated":1750571289000}'),i={name:"nav/index.md"};function o(d,a,n,h,s,c){return l(),e("div",null,a[0]||(a[0]=[r('<h1 id="📢-服务器公告-车站建设推荐性标准" tabindex="-1">📢 服务器公告：车站建设推荐性标准 <a class="header-anchor" href="#📢-服务器公告-车站建设推荐性标准" aria-label="Permalink to &quot;📢 服务器公告：车站建设推荐性标准&quot;">​</a></h1><p>各位玩家：</p><p>为提升车站建设的规范性与美观度，管理组制定了以下<strong>推荐性标准</strong>。遵循此标准可享受专属福利！</p><hr><h2 id="🚉-车站建设推荐标准" tabindex="-1">🚉 车站建设推荐标准 <a class="header-anchor" href="#🚉-车站建设推荐标准" aria-label="Permalink to &quot;🚉 车站建设推荐标准&quot;">​</a></h2><h3 id="一、基础布局" tabindex="-1">一、基础布局 <a class="header-anchor" href="#一、基础布局" aria-label="Permalink to &quot;一、基础布局&quot;">​</a></h3><ul><li><strong>站台尺寸</strong><br> ✅ 建议宽度≥14格<br> ✅ 地下站深度≥15格 | 高架站高度≥6格</li><li><strong>通道设计</strong><br> ⚠️ 至少2条通道（含1扶梯+1楼梯）<br> 🔼 高度差&gt;6格时建议增加直梯</li></ul><h3 id="二、功能设施" tabindex="-1">二、功能设施 <a class="header-anchor" href="#二、功能设施" aria-label="Permalink to &quot;二、功能设施&quot;">​</a></h3><ol><li><strong>必备设施</strong><ul><li>客服中心（3进站闸机+2售票机）</li><li>站名牌（使用MTR Platform Sign）</li></ul></li><li><strong>推荐配置</strong><ul><li>无障碍通道（活板门+地毯）</li><li>LED时刻表（物品展示框+发光方块）</li></ul></li></ol><h3 id="三、设计规范" tabindex="-1">三、设计规范 <a class="header-anchor" href="#三、设计规范" aria-label="Permalink to &quot;三、设计规范&quot;">​</a></h3><table tabindex="0"><thead><tr><th>项目</th><th>推荐参数</th></tr></thead><tbody><tr><td>进站速度</td><td>≤40km/h（红石减速）</td></tr><tr><td>吊顶高度</td><td>≥5格</td></tr><tr><td>照明间隔</td><td>每4格1盏灯</td></tr></tbody></table><hr><h2 id="🎁-奖励机制" tabindex="-1">🎁 奖励机制 <a class="header-anchor" href="#🎁-奖励机制" aria-label="Permalink to &quot;🎁 奖励机制&quot;">​</a></h2><h3 id="申请流程" tabindex="-1">申请流程 <a class="header-anchor" href="#申请流程" aria-label="Permalink to &quot;申请流程&quot;">​</a></h3><ol><li>车站完工后提交截图至QQ群</li><li>标注 <code>#推荐标准审核</code></li><li>管理组48小时内反馈</li></ol><h3 id="专属福利" tabindex="-1">专属福利 <a class="header-anchor" href="#专属福利" aria-label="Permalink to &quot;专属福利&quot;">​</a></h3><ul><li>✅ 定制站台广播（录制专属语音）</li><li>✅ 专属线路图标识</li><li>✅ 建筑材料大礼包</li></ul><hr><h2 id="📅-补充说明" tabindex="-1">📅 补充说明 <a class="header-anchor" href="#📅-补充说明" aria-label="Permalink to &quot;📅 补充说明&quot;">​</a></h2><ol><li>本标准自<strong>2023年XX月XX日</strong>生效</li><li>最终解释权归管理组所有</li><li>建议搭配使用 <code>/kit station_pro</code> 专业建造工具包</li></ol><blockquote><p>💡 小贴士：使用WorldEdit指令 <code>//copy -e</code> 可保留实体数据！</p></blockquote><p><a href="#">点击查看标准车站示例图</a></p>',22)]))}const p=t(i,[["render",o]]);export{b as __pageData,p as default};
